// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { EventDB } from '../event-db'
import { EventService } from '../event.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-eventstable',
  templateUrl: './events-table.component.html',
  styleUrls: ['./events-table.component.css'],
})
export class EventsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of Event instances
  selection: SelectionModel<EventDB> = new (SelectionModel)
  initialSelection = new Array<EventDB>()

  // the data source for the table
  events: EventDB[] = []
  matTableDataSource: MatTableDataSource<EventDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.events
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (eventDB: EventDB, property: string) => {
      switch (property) {
        case 'ID':
          return eventDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return eventDB.Name;

        case 'Duration':
          return eventDB.Duration;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (eventDB: EventDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the eventDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += eventDB.Name.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private eventService: EventService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of event instances
    public dialogRef: MatDialogRef<EventsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.eventService.EventServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getEvents()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Duration",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Duration",
      ]
      this.selection = new SelectionModel<EventDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getEvents()
    this.matTableDataSource = new MatTableDataSource(this.events)
  }

  getEvents(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.events = this.frontRepo.Events_array;

        // insertion point for variables Recoveries
        // compute strings for durations
        for (let event of this.events) {
          event.Duration_string =
            Math.floor(event.Duration / (3600 * 1000 * 1000 * 1000)) + "H " +
            Math.floor(event.Duration % (3600 * 1000 * 1000 * 1000) / (60 * 1000 * 1000 * 1000)) + "M " +
            event.Duration % (60 * 1000 * 1000 * 1000) / (1000 * 1000 * 1000) + "S"
        }

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let event of this.events) {
            let ID = this.dialogData.ID
            let revPointer = event[this.dialogData.ReversePointer as keyof EventDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(event)
            }
            this.selection = new SelectionModel<EventDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, EventDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as EventDB[]
          for (let associationInstance of sourceField) {
            let event = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as EventDB
            this.initialSelection.push(event)
          }

          this.selection = new SelectionModel<EventDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.events
      }
    )
  }

  // newEvent initiate a new event
  // create a new Event objet
  newEvent() {
  }

  deleteEvent(eventID: number, event: EventDB) {
    // list of events is truncated of event before the delete
    this.events = this.events.filter(h => h !== event);

    this.eventService.deleteEvent(eventID).subscribe(
      event => {
        this.eventService.EventServiceChanged.next("delete")
      }
    );
  }

  editEvent(eventID: number, event: EventDB) {

  }

  // display event in router
  displayEventInRouter(eventID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongsim_go-" + "event-display", eventID])
  }

  // set editor outlet
  setEditorRouterOutlet(eventID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongsim_go_editor: ["github_com_fullstack_lang_gongsim_go-" + "event-detail", eventID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(eventID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongsim_go_presentation: ["github_com_fullstack_lang_gongsim_go-" + "event-presentation", eventID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.events.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.events.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<EventDB>()

      // reset all initial selection of event that belong to event
      for (let event of this.initialSelection) {
        let index = event[this.dialogData.ReversePointer as keyof EventDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(event)

      }

      // from selection, set event that belong to event
      for (let event of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = event[this.dialogData.ReversePointer as keyof EventDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(event)
      }


      // update all event (only update selection & initial selection)
      for (let event of toUpdate) {
        this.eventService.updateEvent(event)
          .subscribe(event => {
            this.eventService.EventServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, EventDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedEvent = new Set<number>()
      for (let event of this.initialSelection) {
        if (this.selection.selected.includes(event)) {
          // console.log("event " + event.Name + " is still selected")
        } else {
          console.log("event " + event.Name + " has been unselected")
          unselectedEvent.add(event.ID)
          console.log("is unselected " + unselectedEvent.has(event.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let event = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as EventDB
      if (unselectedEvent.has(event.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<EventDB>) = new Array<EventDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          event => {
            if (!this.initialSelection.includes(event)) {
              // console.log("event " + event.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + event.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = event.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = event.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("event " + event.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<EventDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
