import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { GongsimStatusDB } from '../gongsimstatus-db'
import { GongsimStatusService } from '../gongsimstatus.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-gongsimstatusstable',
  templateUrl: './gongsimstatuss-table.component.html',
  styleUrls: ['./gongsimstatuss-table.component.css'],
})
export class GongsimStatussTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of GongsimStatus instances
  selection: SelectionModel<GongsimStatusDB> = new (SelectionModel)
  initialSelection = new Array<GongsimStatusDB>()

  // the data source for the table
  gongsimstatuss: GongsimStatusDB[] = []
  matTableDataSource: MatTableDataSource<GongsimStatusDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.gongsimstatuss
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (gongsimstatusDB: GongsimStatusDB, property: string) => {
      switch (property) {
        case 'ID':
          return gongsimstatusDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return gongsimstatusDB.Name;

        case 'CurrentCommand':
          return gongsimstatusDB.CurrentCommand;

        case 'CompletionDate':
          return gongsimstatusDB.CompletionDate;

        case 'CurrentSpeedCommand':
          return gongsimstatusDB.CurrentSpeedCommand;

        case 'SpeedCommandCompletionDate':
          return gongsimstatusDB.SpeedCommandCompletionDate;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (gongsimstatusDB: GongsimStatusDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the gongsimstatusDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += gongsimstatusDB.Name.toLowerCase()
      mergedContent += gongsimstatusDB.CurrentCommand.toLowerCase()
      mergedContent += gongsimstatusDB.CompletionDate.toLowerCase()
      mergedContent += gongsimstatusDB.CurrentSpeedCommand.toLowerCase()
      mergedContent += gongsimstatusDB.SpeedCommandCompletionDate.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private gongsimstatusService: GongsimStatusService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of gongsimstatus instances
    public dialogRef: MatDialogRef<GongsimStatussTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.gongsimstatusService.GongsimStatusServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getGongsimStatuss()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "CurrentCommand",
        "CompletionDate",
        "CurrentSpeedCommand",
        "SpeedCommandCompletionDate",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "CurrentCommand",
        "CompletionDate",
        "CurrentSpeedCommand",
        "SpeedCommandCompletionDate",
      ]
      this.selection = new SelectionModel<GongsimStatusDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getGongsimStatuss()
    this.matTableDataSource = new MatTableDataSource(this.gongsimstatuss)
  }

  getGongsimStatuss(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.gongsimstatuss = this.frontRepo.GongsimStatuss_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let gongsimstatus of this.gongsimstatuss) {
            let ID = this.dialogData.ID
            let revPointer = gongsimstatus[this.dialogData.ReversePointer as keyof GongsimStatusDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(gongsimstatus)
            }
            this.selection = new SelectionModel<GongsimStatusDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, GongsimStatusDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as GongsimStatusDB[]
          for (let associationInstance of sourceField) {
            let gongsimstatus = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as GongsimStatusDB
            this.initialSelection.push(gongsimstatus)
          }

          this.selection = new SelectionModel<GongsimStatusDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.gongsimstatuss
      }
    )
  }

  // newGongsimStatus initiate a new gongsimstatus
  // create a new GongsimStatus objet
  newGongsimStatus() {
  }

  deleteGongsimStatus(gongsimstatusID: number, gongsimstatus: GongsimStatusDB) {
    // list of gongsimstatuss is truncated of gongsimstatus before the delete
    this.gongsimstatuss = this.gongsimstatuss.filter(h => h !== gongsimstatus);

    this.gongsimstatusService.deleteGongsimStatus(gongsimstatusID).subscribe(
      gongsimstatus => {
        this.gongsimstatusService.GongsimStatusServiceChanged.next("delete")
      }
    );
  }

  editGongsimStatus(gongsimstatusID: number, gongsimstatus: GongsimStatusDB) {

  }

  // display gongsimstatus in router
  displayGongsimStatusInRouter(gongsimstatusID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongsim_go-" + "gongsimstatus-display", gongsimstatusID])
  }

  // set editor outlet
  setEditorRouterOutlet(gongsimstatusID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongsim_go_editor: ["github_com_fullstack_lang_gongsim_go-" + "gongsimstatus-detail", gongsimstatusID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(gongsimstatusID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongsim_go_presentation: ["github_com_fullstack_lang_gongsim_go-" + "gongsimstatus-presentation", gongsimstatusID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.gongsimstatuss.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.gongsimstatuss.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<GongsimStatusDB>()

      // reset all initial selection of gongsimstatus that belong to gongsimstatus
      for (let gongsimstatus of this.initialSelection) {
        let index = gongsimstatus[this.dialogData.ReversePointer as keyof GongsimStatusDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(gongsimstatus)

      }

      // from selection, set gongsimstatus that belong to gongsimstatus
      for (let gongsimstatus of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = gongsimstatus[this.dialogData.ReversePointer as keyof GongsimStatusDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(gongsimstatus)
      }


      // update all gongsimstatus (only update selection & initial selection)
      for (let gongsimstatus of toUpdate) {
        this.gongsimstatusService.updateGongsimStatus(gongsimstatus)
          .subscribe(gongsimstatus => {
            this.gongsimstatusService.GongsimStatusServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, GongsimStatusDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedGongsimStatus = new Set<number>()
      for (let gongsimstatus of this.initialSelection) {
        if (this.selection.selected.includes(gongsimstatus)) {
          // console.log("gongsimstatus " + gongsimstatus.Name + " is still selected")
        } else {
          console.log("gongsimstatus " + gongsimstatus.Name + " has been unselected")
          unselectedGongsimStatus.add(gongsimstatus.ID)
          console.log("is unselected " + unselectedGongsimStatus.has(gongsimstatus.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let gongsimstatus = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as GongsimStatusDB
      if (unselectedGongsimStatus.has(gongsimstatus.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<GongsimStatusDB>) = new Array<GongsimStatusDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          gongsimstatus => {
            if (!this.initialSelection.includes(gongsimstatus)) {
              // console.log("gongsimstatus " + gongsimstatus.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + gongsimstatus.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = gongsimstatus.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = gongsimstatus.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("gongsimstatus " + gongsimstatus.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<GongsimStatusDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
