// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { UpdateStateDB } from '../updatestate-db'
import { UpdateStateService } from '../updatestate.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-updatestatestable',
  templateUrl: './updatestates-table.component.html',
  styleUrls: ['./updatestates-table.component.css'],
})
export class UpdateStatesTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of UpdateState instances
  selection: SelectionModel<UpdateStateDB> = new (SelectionModel)
  initialSelection = new Array<UpdateStateDB>()

  // the data source for the table
  updatestates: UpdateStateDB[] = []
  matTableDataSource: MatTableDataSource<UpdateStateDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.updatestates
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (updatestateDB: UpdateStateDB, property: string) => {
      switch (property) {
        case 'ID':
          return updatestateDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return updatestateDB.Name;

        case 'Duration':
          return updatestateDB.Duration;

        case 'Period':
          return updatestateDB.Period;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (updatestateDB: UpdateStateDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the updatestateDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += updatestateDB.Name.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private updatestateService: UpdateStateService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of updatestate instances
    public dialogRef: MatDialogRef<UpdateStatesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.updatestateService.UpdateStateServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getUpdateStates()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Duration",
        "Period",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Duration",
        "Period",
      ]
      this.selection = new SelectionModel<UpdateStateDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getUpdateStates()
    this.matTableDataSource = new MatTableDataSource(this.updatestates)
  }

  getUpdateStates(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.updatestates = this.frontRepo.UpdateStates_array;

        // insertion point for variables Recoveries
        // compute strings for durations
        for (let updatestate of this.updatestates) {
          updatestate.Duration_string =
            Math.floor(updatestate.Duration / (3600 * 1000 * 1000 * 1000)) + "H " +
            Math.floor(updatestate.Duration % (3600 * 1000 * 1000 * 1000) / (60 * 1000 * 1000 * 1000)) + "M " +
            updatestate.Duration % (60 * 1000 * 1000 * 1000) / (1000 * 1000 * 1000) + "S"
        }
        // compute strings for durations
        for (let updatestate of this.updatestates) {
          updatestate.Period_string =
            Math.floor(updatestate.Period / (3600 * 1000 * 1000 * 1000)) + "H " +
            Math.floor(updatestate.Period % (3600 * 1000 * 1000 * 1000) / (60 * 1000 * 1000 * 1000)) + "M " +
            updatestate.Period % (60 * 1000 * 1000 * 1000) / (1000 * 1000 * 1000) + "S"
        }

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let updatestate of this.updatestates) {
            let ID = this.dialogData.ID
            let revPointer = updatestate[this.dialogData.ReversePointer as keyof UpdateStateDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(updatestate)
            }
            this.selection = new SelectionModel<UpdateStateDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, UpdateStateDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as UpdateStateDB[]
          for (let associationInstance of sourceField) {
            let updatestate = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as UpdateStateDB
            this.initialSelection.push(updatestate)
          }

          this.selection = new SelectionModel<UpdateStateDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.updatestates
      }
    )
  }

  // newUpdateState initiate a new updatestate
  // create a new UpdateState objet
  newUpdateState() {
  }

  deleteUpdateState(updatestateID: number, updatestate: UpdateStateDB) {
    // list of updatestates is truncated of updatestate before the delete
    this.updatestates = this.updatestates.filter(h => h !== updatestate);

    this.updatestateService.deleteUpdateState(updatestateID).subscribe(
      updatestate => {
        this.updatestateService.UpdateStateServiceChanged.next("delete")
      }
    );
  }

  editUpdateState(updatestateID: number, updatestate: UpdateStateDB) {

  }

  // display updatestate in router
  displayUpdateStateInRouter(updatestateID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongsim_go-" + "updatestate-display", updatestateID])
  }

  // set editor outlet
  setEditorRouterOutlet(updatestateID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongsim_go_editor: ["github_com_fullstack_lang_gongsim_go-" + "updatestate-detail", updatestateID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(updatestateID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongsim_go_presentation: ["github_com_fullstack_lang_gongsim_go-" + "updatestate-presentation", updatestateID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.updatestates.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.updatestates.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<UpdateStateDB>()

      // reset all initial selection of updatestate that belong to updatestate
      for (let updatestate of this.initialSelection) {
        let index = updatestate[this.dialogData.ReversePointer as keyof UpdateStateDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(updatestate)

      }

      // from selection, set updatestate that belong to updatestate
      for (let updatestate of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = updatestate[this.dialogData.ReversePointer as keyof UpdateStateDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(updatestate)
      }


      // update all updatestate (only update selection & initial selection)
      for (let updatestate of toUpdate) {
        this.updatestateService.updateUpdateState(updatestate)
          .subscribe(updatestate => {
            this.updatestateService.UpdateStateServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, UpdateStateDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedUpdateState = new Set<number>()
      for (let updatestate of this.initialSelection) {
        if (this.selection.selected.includes(updatestate)) {
          // console.log("updatestate " + updatestate.Name + " is still selected")
        } else {
          console.log("updatestate " + updatestate.Name + " has been unselected")
          unselectedUpdateState.add(updatestate.ID)
          console.log("is unselected " + unselectedUpdateState.has(updatestate.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let updatestate = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as UpdateStateDB
      if (unselectedUpdateState.has(updatestate.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<UpdateStateDB>) = new Array<UpdateStateDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          updatestate => {
            if (!this.initialSelection.includes(updatestate)) {
              // console.log("updatestate " + updatestate.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + updatestate.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = updatestate.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = updatestate.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("updatestate " + updatestate.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<UpdateStateDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
